CREATE DATABASE IF NOT EXISTS gold;
set path_silver_d_products = 'abfss://silver@bigpharma.dfs.core.windows.net/d_products';
set path_silver_d_customers = 'abfss://silver@bigpharma.dfs.core.windows.net/d_customers';
set path_gold_d_products = 'abfss://gold@bigpharma.dfs.core.windows.net/d_products';
set path_gold_d_customers = 'abfss://gold@bigpharma.dfs.core.windows.net/d_customers';


-- Załadowanie danych z warstwy Silver do tabeli Delta
-- Tworzenie tabel Delta w warstwie Silver
CREATE OR REPLACE VIEW silver_d_products AS
SELECT * FROM delta.`${path_silver_d_products}`;

CREATE OR REPLACE view silver_d_regions as
SELECT * FROM delta.`${path_silver_d_customers}`;

-- Tworzenie tabel w warstwie Gold
CREATE TABLE IF NOT EXISTS gold.d_products
USING DELTA
LOCATION ${path_gold_d_products}
AS
SELECT * FROM silver_d_products
WHERE 1 = 0;


CREATE TABLE IF NOT EXISTS TABLE gold.d_regions
USING DELTA
LOCATION ${path_gold_d_customers}
AS
SELECT * FROM silver_d_regions
WHERE 1 = 0;


-- Zmiana nazw kolumn w tabeli d_products i zapis do warstwy Gold
INSERT OVERWRITE TABLE gold.d_products
SELECT 
    product_id AS IdProduct,
    product_name AS Name,
    brand_name AS Brand,
    sub_brand_name AS SubBrand,
    category_name AS Category
FROM silver.d_products;

-- Usuwanie duplikatów w tabeli d_regions i zapis do nowej tabeli Gold
INSERT OVERWRITE TABLE gold.d_regions
SELECT DISTINCT 
    country_id AS IDCountry,
    cluster AS Claster,
    region_description AS Region,
    country AS Country
FROM silver_d_regions;
